#!/usr/bin/env bash

__deezer() {
    local DEEZER_HOME=$HOME/.deezerloadermix
    local DIR_ORIGINAL=$(pwd)
    local BANNER=""
    
    if [ -d "$DEEZER_HOME" ]; then
        echo $BANNER | base64 -d
        echo
        cd "$DEEZER_HOME" && npm start
        cd "$DIR_ORIGINAL"
    else
        echo "no found Directory"
    fi
}

